package se.oshu.perfectnumber;import java.util.ArrayList;import java.util.List;class PerfectNumberCalculators {    /**     * Checks if a value is a Perfect Number.     * @param number The value to be calculated.     * @return True if the value is a Perfect Number.     */    static boolean isPerfectNumber(long number) {        int temp = 0;        for (int i = 1; i <= number / 2; i++) {            if (number % i == 0) {                temp += i;            }        }        if (temp == number) {            return true;        } else {            return false;        }    }    /**     * Calculates all the Perfect Numbers in given range.     * @param userInputLowRangeNumber Low range value.     * @param userInputHighRangeNumber High range value.     * @return A List with the PerfectNumbers.     */    static List<Long> getAllPerfectNumbersInRange(Long userInputLowRangeNumber, Long userInputHighRangeNumber) {        List<Long> perfectNumbersInSpecifiedRange = new ArrayList<>();        for (long i = userInputLowRangeNumber; i <= userInputHighRangeNumber; i++) {            if (PerfectNumberCalculators.isPerfectNumber(i)) {                perfectNumbersInSpecifiedRange.add(i);            }        }        return perfectNumbersInSpecifiedRange;    }}