package se.oshu.perfectnumber;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.util.HashMap;import java.util.List;import java.util.Map;@RestControllerclass PerfectNumberController {    /**     * Recieves a request checking if number is perfect.     * @param userInputNumber A value from user.     * @return A ResponseEntity<Map>.     */    @GetMapping(value = "/is_perfect_number", produces = "application/json")    public ResponseEntity<Map> perfectnumber(@RequestParam(value = "number") Long userInputNumber) {        Map toBeReturned = new HashMap<>();        toBeReturned.put("perfectNumber", PerfectNumberCalculators.isPerfectNumber(userInputNumber));        return ResponseEntity.ok().body(toBeReturned);    }    /**     * Recieves a request for Perfect Numbers within a range.     * @param userInputLowRangeNumber A low range value from the user.     * @param userInputHighRangeNumber A high range value from the user.     * @return A ResponseEntity<List<Long>>.     */    @GetMapping(value = "/perfect_numbers", produces = "application/json")    public ResponseEntity<List<Long>> perfectNumbers(@RequestParam(value = "lowRange") Long userInputLowRangeNumber,                                                    @RequestParam(value = "highRange") Long userInputHighRangeNumber) {        List<Long> toBeReturned = PerfectNumberCalculators.getAllPerfectNumbersInRange(userInputLowRangeNumber, userInputHighRangeNumber);        return ResponseEntity.ok().body(toBeReturned);    }}