package se.oshu.perfectnumber;import org.junit.Test;import java.util.ArrayList;import java.util.List;import static org.junit.Assert.*;public class PerfectNumberCalculatorsTest {    @Test    public void isPerfectNumberTest() {        assertEquals(true, PerfectNumberCalculators.isPerfectNumber(6));        assertEquals(false, PerfectNumberCalculators.isPerfectNumber(5));    }    @Test    public void getAllPerfectNumbersInRangeTest() {        List<Long> perfectNumbersInSpecifiedRange = new ArrayList<>();        perfectNumbersInSpecifiedRange.add(6l);        perfectNumbersInSpecifiedRange.add(28l);        assertEquals(perfectNumbersInSpecifiedRange,                PerfectNumberCalculators.getAllPerfectNumbersInRange(5l, 30l));    }}